import nltk
import sklearn_crfsuite
from sklearn_crfsuite import metrics
from sklearn_crfsuite import CRF
from sklearn.model_selection import train_test_split
from nltk.tokenize import word_tokenize
from nltk.corpus import floresta

nltk.download('floresta')

# Load the tagged sentences from the Floresta dataset
tagged_sents = floresta.tagged_sents()

# Split the dataset into training and testing sets
train_sents, test_sents = train_test_split(tagged_sents, test_size=0.2, random_state=42)

# Define a function to extract features from a sentence
def word2features(sent, i):
    word = sent[i][0]
    features = {
        'bias': 1.0,
        'word.lower()': word.lower(),
        'word[-3:]': word[-3:],
        'word[-2:]': word[-2:],
        'word.isupper()': word.isupper(),
        'word.istitle()': word.istitle(),
        'word.isdigit()': word.isdigit(),
    }
    if i > 0:
        word1 = sent[i-1][0]
        features.update({
            '-1:word.lower()': word1.lower(),
            '-1:word.istitle()': word1.istitle(),
            '-1:word.isupper()': word1.isupper(),
        })
    else:
        features['BOS'] = True

    if i < len(sent)-1:
        word1 = sent[i+1][0]
        features.update({
            '+1:word.lower()': word1.lower(),
            '+1:word.istitle()': word1.istitle(),
            '+1:word.isupper()': word1.isupper(),
        })
    else:
        features['EOS'] = True

    return features

# Define a function to extract features from a sentence
def sent2features(sent):
    return [word2features(sent, i) for i in range(len(sent))]

def sent2labels(sent):
    return [label.encode('utf-8') for token, label in sent]

# Extract features and labels from the training set
X_train = [sent2features(s) for s in train_sents]
y_train = [sent2labels(s) for s in train_sents]

# Extract features and labels from the testing set
X_test = [sent2features(s) for s in test_sents]
y_test = [sent2labels(s) for s in test_sents]

# Create and train the CRF model with encoding='utf-8'
crf_model = CRF()
crf_model.fit(X_train, y_train)

# Set the keep_tempfiles attribute explicitly to False after fitting
crf_model.keep_tempfiles = False

# Make predictions on the test set
y_pred = crf_model.predict(X_test)

# Print evaluation metrics
print(metrics.flat_classification_report(y_test, y_pred))

# Tag the given sentence
your_sentence = "Até breve!"
tokenized_sentence = word_tokenize(your_sentence)
features = [word2features(tokenized_sentence, i) for i in range(len(tokenized_sentence))]
predicted_tags = crf_model.predict_single(features)
# Print the POS tagging results for the given sentence
for word, pred_tag in zip(tokenized_sentence, predicted_tags):
    print(f"Word: {word}\tPredicted Tag: {pred_tag}")

import numpy
from nltk.corpus import treebank
from nltk.tag import hmm
import nltk

# Download necessary NLTK resources
nltk.download('treebank')
nltk.download('punkt')

# Define state matrix, emission matrix, and initial matrix
stateMatrix = numpy.matrix("0.7 0.3; 0.5 0.5")
emissionMatrix = numpy.matrix("0.6 0.1 0.3; 0.1 0.7 0.2")
initialMatrix = numpy.matrix("1 0")

# Initialize HMM trainer
trainer = hmm.HiddenMarkovModelTrainer()

# Train HMM tagger on the Treebank corpus
tagged_sentences = treebank.tagged_sents()  # Get tagged sentences from the Treebank corpus
tagger = trainer.train_supervised(tagged_sentences)

# Tokenize a sample sentence
